// 0 = wall; 1 = corridor; 2+ mobs
#define defined_map ""
// Let's define some structures describing game environment;

typedef struct
{
    char nick[16];
    bool alive;
    int infight;
    int lvl;
    int x;
    int y;
} Player;

typedef struct
{
    char *name;
    bool alive;
    int lvl;
} Mob;

typedef struct
{
    int N;
    int S;
    int W;
    int E;
} Room;

typedef struct
{
    char *name;
    int x;
    int y;
    int start_x;
    int start_y;
    int exit_x;
    int exit_y;
    char *rooms;
} Map;

typedef struct
{
    Player player;
    Map map;
    Mob *mobs;
} World;

// Now some functions handling those structures;

Player loadPlayer()
{
    Player Steve;
    strcpy(Steve.nick, "Steve");
    Steve.alive = true;
    Steve.infight = -1;
    Steve.lvl = 0;
    Steve.x = -1;
    Steve.y = -1;
    return Steve;
}

Map loadMap()
{
    Map Dungeon;
    Dungeon.name = "Dungeon";
    Dungeon.x = 119;
    Dungeon.y = 21;
    Dungeon.start_x = 59;
    Dungeon.start_y = 1;
    Dungeon.exit_x = 59;
    Dungeon.exit_y = 20;
    Dungeon.rooms = defined_map;
    return Dungeon;
}

Mob* loadMobs()
{
    Mob *mobs = malloc(10 * sizeof(Mob));

    mobs[0].name = "Rat";
    mobs[0].lvl = 0;
    mobs[0].alive = true;

    mobs[1].name = "Red-eyed Rat";
    mobs[1].lvl = 1;
    mobs[1].alive = true;

    mobs[2].name = "Velociraptor";
    mobs[2].lvl = 2;
    mobs[2].alive = true;

    mobs[3].name = "Skalmar";
    mobs[3].lvl = 3;
    mobs[3].alive = true;

    mobs[4].name = "Spongebob Squarepants";
    mobs[4].lvl = 4;
    mobs[4].alive = true;

    mobs[5].name = "John Wane";
    mobs[5].lvl = 5;
    mobs[5].alive = true;

    mobs[6].name = "Crocodile Dundee";
    mobs[6].lvl = 6;
    mobs[6].alive = true;

    mobs[7].name = "Godzilla";
    mobs[7].lvl = 7;
    mobs[7].alive = true;

    mobs[8].name = "Donkey The Kong";
    mobs[8].lvl = 8;
    mobs[8].alive = true;

    mobs[9].name = "Shrek The Ogre";
    mobs[9].lvl = 9;
    mobs[9].alive = true;

    return mobs;
}
